import React,{useId} from 'react';
import './banner.css';
import "react-responsive-carousel/lib/styles/carousel.min.css";

import { Carousel } from 'react-responsive-carousel';


function CustomCarousel() {

  const id = useId();

  const data = [
    {
      text : "Welcome to VKEdu",
      image:  "assets/b1.jpg"

    },
    {
      text : "Your future starts with VKEdu",
      image:  "assets/b2.jpg"

    },

  ]

  return (
    <div className='home__banner'>
      <Carousel interval="8000" transitionTime="1000"
       autoPlay infiniteLoop showStatus={false} showThumbs={false} width="100vw">
        {
          data.map(item =>
            <div key={id} style={{back:item.image}}>
              <div>
                <h2>{item.text}</h2>
                <button>Start a Journey</button>
              </div>
            </div>
            )
        }
      </Carousel>
    </div>
  )
}

export default CustomCarousel
