import React from 'react';
import './banner.css';
import { Carousel } from 'react-responsive-carousel';


function CustomCarousel() {

  const data = [
    {
      text : "Welcome to VKEdu",
      image:  "assets/bg1.jpg"

    },
    {
      text : "Your future starts with VKEdu",
      image:  "assets/bg2.jpg"

    },
    {
      text : "Be a achiever",
      image:  "assets/bg3.jpg"

    },

  ]

  return (
    <div className='home__banner'>
      <Carousel interval="3000" transitionTime="1000"
       autoPlay infiniteLoop showStatus={false} showThumbs={false} width="100vw">
        {
          data.map(item =>
            <div bac>
              <div>
                <h2>{item.text}</h2>
                <button>Start a Journey</button>
              </div>
            </div>
            )
        }
      </Carousel>
    </div>
  )
}

export default CustomCarousel
